mapper.select:
select
  id, name, address, phone, email, remark
from merchants_info


        and id = #{id}


        and name = #{name}


        and address = #{address}


        and phone = #{phone}


        and email = #{email}


        and remark = #{remark}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from merchants_info


        and id = #{id}


        and name = #{name}


        and address = #{address}


        and phone = #{phone}


        and email = #{email}


        and remark = #{remark}
