mapper.select:
select
  id, merchant_id, paint_id, price, remark
from supply_relations


        and id = #{id}


        and merchant_id = #{merchantId}


        and paint_id = #{paintId}


        and price = #{price}


        and remark = #{remark}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from supply_relations


        and id = #{id}


        and merchant_id = #{merchantId}


        and paint_id = #{paintId}


        and price = #{price}


        and remark = #{remark}
